package javaadvance;

public class Bitwiseoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=5;
		
		System.out.println("a&b:"+(a&b));
		System.out.println("a|b:"+(a|b));
		System.out.println("a^b:"+(a^b));
		System.out.println("a&=b:"+(a&=b));
		System.out.println("~a:"+ ~a);
		System.out.println("~b:"+ ~b);

	}

}
