package javaadvance;

public class Comparsionoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=3;
		int b=5;
		
		if(a!=b) {
			System.out.println("a!=b:");
		}
		
		if(a==b) {
			System.out.println("a==b:");
		}
		
		if(a>b) {
			System.out.println("a>b:");
		}
		
		if(a<b) {
			System.out.println("a<b:");
		}
		
		if(a>=b) {
			System.out.println("a>=b:");
		}
		
		if(a<=b) {
			System.out.println("a<=b:");
		}
		

	}

}
