package javaadvance;

public class Logicaloperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=1;
		int b=2;
		
		if((a==1)&&(b==2)) {
			System.out.println("a is 1 && b is 2");
		}
		
		if((a==1)||(b==2)) {
			System.out.println("a is 1 || b is 2");
		}
		
		

	}

}
